        // });

        // $(".hs-next").click(function(e) {
        //     e.preventDefault();
        //     var slider = $("#hero-home").flickity();
        //     slider.flickity('next');
        // });

        initSlider('produk-pendukung-slider-prime', {
            group: 2,
            isMobileOnly: true
        });
        initSlider('produk-pendukung-slider-prime-plus', {
            group: 2,
            isMobileOnly: true
        });
        initSlider('produk-pendukung-slider', {
            group: 2,
            isMobileOnly: true
        });
    });
